    render() {
        const { albums } = this.state
        return (
            <div>
                <button>{ albums.length }</button>
                <ul>
                 {albums.length <= 0
                     ? 'NO DB ENTRIES YET'
                     : albums.map((dat) => (
                         <li style={{ padding: '10px' }} key={albums._id}>
                             <span style={{ color: 'gray' }}> id: </span> {dat._id} <br />
                             <span style={{ color: 'gray' }}> category: </span> {dat.title}
                        </li>
            ))}
                </ul>

            </div>
    );
    }
}

export default App;
